<template>
	<view class="content">
		<unicloud-db ref="udb" v-slot:default="{data, pagination, loading, hasMore, error}" :collection="collectionList" field="title,photo">
		  <view v-if="error">{{error.message}}</view>
		  <view v-else-if="data">
		    <uni-list class="list">
		      <uni-list-item v-for="(item, index) in data" :key="index" :class="{active: index===num}">
		        <template v-slot:body>
		          <view class="item">
		            <!-- 此处默认显示为_id，请根据需要自行修改为其他字段 -->
		            <!-- 如果使用了联表查询，请参考生成的 admin 项目中 list.vue 页面 -->
		           <view>
					<image :src="item.photo.url" mode="aspectFill"></image>
					</view>
					<view>
						{{item.title}}
					</view>
		          </view>
		        </template>
		      </uni-list-item>
		    </uni-list>
		  </view>
		  <uni-load-more :status="loading?'loading':(hasMore ? 'more' : 'noMore')"></uni-load-more>
		</unicloud-db>
		<view class="button-area">
			<button v-show="isOver" @click="start">开始抽奖</button>
			<button v-show="!isOver" @click="stop">结束抽奖</button>
		</view>
	</view>
</template>

<script >
	const db = uniCloud.database();
	const dbCollectionName = 'lottery';
	//lottery.schema里面的数据
	export default {
		data() {
			return {
				collectionList:dbCollectionName,
				num:0,
				max:0,
				interval:100,
				isOver:true,
				clock:null,
				loadMore:{
					contentdown:'',
					contentrefresh:'',
					contentnomore:'',
				}
			}
		},
		onLoad() {

		},
		onShow(){
			// 当 uni-app 启动，或从后台进入前台显示
			this.$forceUpdate()
		},
		created(){
			this.$nextTick(()=>{
				db.collection(dbCollectionName)
				 .get()
				 .then((res)=>{
					 this.max=res.result.data.length
				 })
			})
		},
		onPullDownRefresh() {
		  this.$refs.udb.loadData({
		    clear: true
		  }, () => {
		    uni.stopPullDownRefresh()
		  })
		},
		onReachBottom() {
		  this.$refs.udb.loadMore()
		},
		methods: {
           start(){
				 this.isOver = false
				 this.clock=setInterval(()=>{
					 this.num=this.getRandomNum()
					 this.num=num
				 },this.interval)
			},
			stop(){
				clearInterval(this.clock)
				this.isOver=true
			},
			getRandomNum(){
				return Math.floor(Math.random()*(parseInt(this.max)))
			}
		}
	}
</script>

